import React, { useEffect, useState } from 'react';

const DonneesTable = () => {
    const [donnees, setDonnees] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:8000/api/donnees')
            .then(response => response.json())
            .then(data => {
                setDonnees(data.factures); // Assurez-vous que la clé 'factures' est correcte
            })
            .catch(error => {
                console.error('Erreur lors de la récupération des données:', error);
            });
    }, []);

    return (
        <div>
            <h1>Données</h1>
            <table style={{borderCollapse: 'collapse', width: '100%'}}>
                <thead>
                    <tr>
                        <th>Numéro de Facture</th>
                        <th>Montant HT</th>
                        <th>Date Facture</th>
                        <th>Taux</th>
                        <th>TVA</th>
                        <th>MontantTTC</th>
                        <th>TypeContrat</th>
                        <th>EtabliPar</th>
                        <th>ÉtatPayement</th>
                        <th>ModeReg</th>
                        <th>NomClient</th>
                        <th>NumBonLiv</th>
                        <th>DateBonLiv</th>
                        <th>TypeValidation</th>
                        <th>NomEmetteur</th>
                        <th>NumCheque</th>
                        <th>MontantEnc</th>
                    </tr>
                </thead>
                <tbody>
                    {donnees.map((facture, index) => (
                        <tr key={index} style={{border: '1px solid black'}}>

                            <td>{facture.client?.NomClient}</td>
                            <td>{facture.bonLivraison?.NumBonLiv}</td>
                            <td>{facture.bonLivraison?.dateBonLiv}</td>
                            <td>{facture.bonLivraison?.TypeValidation}</td>
                            <td>{facture.emetteur?.NomEmetteur}</td>
                            <td>{facture.cheque?.NumCheque}</td>
                            <td>{facture.remise?.MontantEnc}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default DonneesTable;
