import React, { useState } from 'react';
import Table from './Table';
import 'C:/Users/Asus ZenBook 14/Desktop/My-github/Invoice Project/src/css/Visualiser.css';

const Visualiser = () => {
  const [filtrageItem, setItemFiltrage] = useState('Valeur:');
  const [showInfo, setShowInfo] = useState(false);
  const [selectedRowData, setSelectedRowData] = useState(null);

  const handleChangefiltrage = (e) => {
    e.preventDefault();
    setItemFiltrage(e.target.value);
  };

  const handleShowInfo = (rowData) => {
    setSelectedRowData(rowData);
    setShowInfo(true);
  };

  const handleCloseInfo = () => {
    setShowInfo(false);
    setSelectedRowData(null);
  };

  const data = [
    // Your data array
  ];

  return (
    <div>
      <center>
        <h2>Visualiser Facture</h2>
      </center>
      <div className="form-group">
        <label htmlFor="selectOption">Element de Filtrage:</label>
        <select id="selectOption" className="form-control" onChange={(e) => handleChangefiltrage(e)}>
          <option value="Valeur:">[Choisir Element de Filtrage]</option>
          <option value="Nº de Facture:">Nº de Facture</option>
          <option value="Nom Client:">Nom Client</option>
          <option value="Nº Bon de livraison:">Nº Bon de livraison</option>
          <option value="Type de validation:">Type de validation</option>
          <option value="Mode de Règlement:">Mode de Règlement</option>
        </select>
        <label htmlFor="textInput">{filtrageItem}</label>
        <input
          type="text"
          className="form-control"
          id="textInput"
          placeholder="Enter value"
          disabled={filtrageItem === 'Valeur:'}
        />
      </div>
      <Table data={data} itemsPerPage={6} onShowInfo={handleShowInfo} />
      {showInfo && (
        <div className="info-modal">
          <div className="info-content">
            <button className="close-btn" onClick={handleCloseInfo}>
              Fermer
            </button>
            {selectedRowData && (
              <div>
                <h3>Informations supplémentaires</h3>
                <p>{/* Render additional information from selectedRowData */}</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Visualiser;
