import React from "react";
import '../css/Menu.css';
import { FiLogOut } from 'react-icons/fi';

import axios from 'axios';




const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      const token = localStorage.getItem('auth_token');
      if (!token) {
        console.log("No auth token found. User is not logged in.");
        return;
      }

      try {
        const response = await axios.get('http://localhost:8000/api/user', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        // Check if response.data is an array
        if (Array.isArray(response.data)) {
          setUsers(response.data);
        } else if (typeof response.data === 'object' && response.data !== null) {
          // If response.data is an object, extract the users array from it
          const usersArray = Object.values(response.data);
          setUsers(usersArray);
        } else {
          console.error('Expected an array or object for users, but received:', response.data);
          setUsers([]);
        }
      } catch (error) {
        console.error('Error fetching users', error);
        setUsers([]);
      }
    };

    fetchUsers();
  }, []);
const handleLogout = async () => {
    try {
      await axios.post('http://localhost:8000/api/logout', null, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('auth_token')}` // Ajoutez le jeton d'authentification à l'en-tête
        }
      });
      // Effacez le jeton d'authentification du localStorage après la déconnexion réussie
      localStorage.removeItem('auth_token');
      // Redirigez l'utilisateur vers la page de connexion ou une autre page appropriée après la déconnexion réussie
      window.location.href = '/';
    } catch (error) {
      console.error('Logout failed', error);
    }
  };
  

