import React, { useState,useEffect } from "react";
import { useDispatch,useSelector } from "react-redux";
import { AjouterFilm , AjouterRealisateur } from "../Redux/actions";

export default function (){
    const [CodeFilm,setCodeFilm]=useState();
    const [Titre,setTitre]=useState();
    const [Durre,setDurre]=useState();
    const [NumRealisateur,setNumRealisateur]=useState();
    const [NomRealisateur,setNomRealisateur]=useState();
    const [PrenomRealisateur,setPrenomRealisateur]=useState();

    const films=useSelector((state)=>state.Films)

    const dispatch=useDispatch()
    useEffect(()=>{console.log(films)})
    const handleSubmit=(e)=>{
        e.preventDefault();
        dispatch(AjouterFilm({codeF:CodeFilm,titre:Titre,durre:Durre,realisateur:NumRealisateur}));
        dispatch(AjouterRealisateur({numR:NumRealisateur,nom:NomRealisateur,prenom:PrenomRealisateur}))
        
    }
    
    return(
        <>
            <form onSubmit={(e)=>handleSubmit(e)}>
                <label>Code Film</label>
                <input type='text' name="CodeFilm" onChange={(e)=>setCodeFilm(e.target.value)}/>
                <label>Titre</label>
                <input type='text' name="Titre" onChange={(e)=>setTitre(e.target.value)}/>
                <label>Durre</label>
                <input type='text' name="Durre" onChange={(e)=>setDurre(e.target.value)}/>
                <label>Num Realisateur</label>
                <input type='text' name="NumRealisateur" onChange={(e)=>setNumRealisateur(e.target.value)}/>
                <label>Nom Realisateur</label>
                <input type='text' name="NomRealisateur" onChange={(e)=>setNomRealisateur(e.target.value)}/>
                <label>Prenom Realisateur</label>
                <input type='text' name="PrenomRealisateur" onChange={(e)=>setPrenomRealisateur(e.target.value)}/>
                <button>ADD FILLM</button>
            </form>
        </>
    )
}
