import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';

const ProtectedRoute = () => {
  const isAuthenticated = () => {
    const token = localStorage.getItem('token'); // Récupérer le jeton depuis le localStorage
    // Vérifier si le jeton est présent et s'il est valide (par exemple, en vérifiant sa signature)
    return token !== null; // Renvoie vrai si le jeton est présent, faux sinon
  };

  return isAuthenticated() ? <Outlet /> : <Navigate to="/" />;
};

export default ProtectedRoute; // Assurez-vous d'exporter le composant par défaut
