import React, { useState } from 'react';
import '../css/GestionCompte.css'; // Assurez-vous d'avoir ce fichier CSS dans le même dossier

const GestionCompte = () => {
    const [formVisible, setFormVisible] = useState(false);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleFormSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ name, email, password }),
            });

            if (response.ok) {
                // Succès de l'enregistrement, peut-être rediriger ou afficher un message de succès
                console.log('Utilisateur enregistré avec succès !');
            } else {
                // Échec de l'enregistrement, gérer les erreurs ou afficher un message d'erreur
                console.error('Erreur lors de l\'enregistrement de l\'utilisateur.');
            }
        } catch (error) {
            console.error('Erreur lors de la requête:', error);
        }
    };

    const toggleFormVisibility = () => {
        setFormVisible(!formVisible);
    };

    return (
        <div className="GestionCompte">
            <div className={`container ${formVisible ? "smaller" : ""}`}>
                <div className={`box add ${formVisible ? 'active' : ''}`} onClick={toggleFormVisibility}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="4em" height="4em" viewBox="0 0 24 24"><path fill="none" stroke="white" strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M17 10h3m3 0h-3m0 0V7m0 3v3M1 20v-1a7 7 0 0 1 7-7v0a7 7 0 0 1 7 7v1m-7-8a4 4 0 1 0 0-8a4 4 0 0 0 0 8"></path></svg>
                    <div>Créer un utilisateur</div>
                </div>
            </div>
            {formVisible && (
                <form className="form" onSubmit={handleFormSubmit}>
                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="nom">Nom</label>
                            <input id="nom" type="text" placeholder="Nom" value={name} onChange={(e) => setName(e.target.value)} required />
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Adresse Email</label>
                            <input id="email" type="email" placeholder="Adresse Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                        </div>
                    </div>
                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="password1">Mot de passe</label>
                            <input id="password1" type="password" placeholder="Mot de passe" value={password} onChange={(e) => setPassword(e.target.value)} required />
                        </div>
                    </div>
                    <button type="submit">Enregistrer</button>
                </form>
            )}
        </div>
    );
};

export default GestionCompte;
