//         const token = authTokenCookie.split('=')[1];
//         return true;
//       }
//       return alert(false);
//     };
//     return isAuthenticated() ? children : navigate('/');
// };
  
export const router = createBrowserRouter([
    {
        path: '/',
        element: <Login />,
        index: true
    },
    {
        path: '/',
        element: <ProtectedRoute><Layout/></ProtectedRoute>,
        children: [
            {
                path: '/Enregistrer',
                element: <Invoice />
            },
            {
                path: '/Visualiser',
                element: <Visualiser/>
            },
            {
                path: '/Suivi',
                element: <Suivi />
            },
            {
                path: '/Gestion des comptes',
                element: <GestionCompte />
            }
        ]
    },
    {
        path: '*',
        element: <PageNotFound />
    }
]);
