import { createBrowserRouter, Navigate } from 'react-router-dom';
import React from 'react';
import Login from '../component/login';
import PageNotFound from '../pages/pageNotFound';
import Layout from '../layout/layout';
import Invoice from '../component/Invoice';
import MainVisualiser from '../component/MainVisualiser';
import GestionCompte from '../component/GestionCompte';

// Router avec ProtectedRoute et Outlet
export const router = createBrowserRouter([
  {
    path: '/',
    element: <Layout />,
    children: [
      { path: '/invoice', element: <ProtectedRoute><Invoice /></ProtectedRoute> },
      { path: '/MainVisualiser', element: <ProtectedRoute><MainVisualiser /></ProtectedRoute> },
      { path: '/GestionCompte', element: <ProtectedRoute><GestionCompte /></ProtectedRoute> }
    ]
  },
  { path: '/', element: <Login />, index: true },

  { path: '*', element: <PageNotFound /> }
]);

// Définition de ProtectedRoute
const ProtectedRoute = ({ children }) => {
  const isAuthenticated = () => {
    return localStorage.getItem('auth_token') !== null;
  };

  // Utilisation de Navigate pour rediriger vers la page de connexion si l'utilisateur n'est pas authentifié
  return isAuthenticated() ? children : <Navigate to="/" />;
};
