import { createBrowserRouter ,Navigate } from "react-router-dom";
import Cookies from 'js-cookie';

import Invoice from './Components/Invoice';
import Login from './Components/Login';
import Visualiser from "./Components/Visualiser/Visualiser";
import Suivi from "./Components/Suivi/Suivi";
import GestionCompte from "./Components/GestionComptes/GestionCompte";
import PageNotFound from "./Components/errors/PageNotFound";
import Layout from "./Components/Header/Layout";

const RequireAuth = ({ children }) => {
    const jwt = Cookies.get('jwt');
    alert(jwt)
    // Rediriger vers Login si le JWT n'existe pas
    if (!jwt) {
        return <Navigate to="/" replace />;
    }

    return children;
};


export const router = createBrowserRouter([
    {
        path: '/',
        element: <Login />
    },
    {
        element: <Layout />,
        children: [
            {
                path: '/Visualiser',
                element: <Visualiser />,
            },
            {
                path: '/Enregistrer',
                element: <RequireAuth><Invoice /></RequireAuth>
            },
            {
                path: '/Suivi',
                element: <Suivi />
            },
            {
                path: '/Gestion des comptes',
                element: <GestionCompte />
            }
        ]
    },
    {
        path: '*',
        element: <PageNotFound />
    },
]);
