import React, { useState } from 'react';
import './Form.css'; // Assurez-vous de créer un fichier Form.css pour les styles

const FormComponent = () => {
  const [formData, setFormData] = useState({
    input1: '',
    input2: '',
    // Ajoutez les autres champs d'entrée de la même manière
    // input3: '', input4: '', ..., input16: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ajoutez ici la logique pour soumettre le formulaire
    console.log(formData);
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <input
        type="text"
        className="input"
        name="input1"
        value={formData.input1}
        onChange={handleChange}
        placeholder="Input 1"
      />
      <input
        type="text"
        className="input"
        name="input2"
        value={formData.input2}
        onChange={handleChange}
        placeholder="Input 2"
      />
      {/* Ajoutez les autres champs d'entrée de la même manière */}
      {/* <input type="text" className="input" name="input3" value={formData.input3} onChange={handleChange} placeholder="Input 3" /> */}
      {/* <input type="text" className="input" name="input4" value={formData.input4} onChange={handleChange} placeholder="Input 4" /> */}
      {/* ... */}
      {/* <input type="text" className="input" name="input16" value={formData.input16} onChange={handleChange} placeholder="Input 16" /> */}
      <div className="button-container">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default Invoice;
