import React, { useState, useEffect } from 'react';
import '../css/GestionCompte.css';

const GestionCompte = () => {
    const [csrfToken, setCsrfToken] = useState('');
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        role: '',
    });

    useEffect(() => {
        fetch('/sanctum/csrf-cookie').then(response => {
            const token = document.querySelector('meta[name="csrf-token"]').getAttribute('content');
            setCsrfToken(token);
        });
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          const response = await fetch('http://127.0.0.1:8000/api/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
                'X-CSRF-TOKEN': csrfToken,
            },
            credentials: 'include', // Ensure cookies, including CSRF tokens, are sent
            body: JSON.stringify(formData),
        });

            const jsonResponse = await response.json();
            console.log(jsonResponse);
        } catch (error) {
            console.error('Error during submission:', error);
        }
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    return (
        <div className="GestionCompte">
            <form className="form" onSubmit={handleSubmit}>
                <div className="form-row">
                    <div className="form-group">
                        <label htmlFor="nom">Nom</label>
                        <input onChange={handleInputChange} value={formData.name} name="name" id="nom" type="text" placeholder="Nom" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Adresse Email</label>
                        <input onChange={handleInputChange} value={formData.email} name="email" id="email" type="email" placeholder="Adresse Email" required />
                    </div>
                </div>
                <div className="form-row">
                    <div className="form-group">
                        <label htmlFor="role">Role</label>
                        <input onChange={handleInputChange} value={formData.role} name="role" id="role" type="text" placeholder="Role" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password1">Mot de passe</label>
                        <input onChange={handleInputChange} value={formData.password} name="password" id="password1" type="password" placeholder="Mot de passe" required />
                    </div>
                </div>
                <button type="submit">Enregistrer</button>
            </form>
        </div>
    );
}

export default GestionCompte;
