import React, { createContext, useContext, useState, useEffect } from 'react';
import Cookies from 'js-cookie';

const AuthContext = createContext({ isAuthenticated: false, setAuthStatus: () => {} });

export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        const token = Cookies.get('authToken');
        setIsAuthenticated(!!token);
    }, []);

    const setAuthStatus = (status) => {
        setIsAuthenticated(status);
        if (status) {
            Cookies.set('authToken', 'yourTokenHere'); // You might want to use an actual token here
        } else {
            Cookies.remove('authToken');
        }
    };

    return (
        <AuthContext.Provider value={{ isAuthenticated, setAuthStatus }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
