import React,{useState} from "react";
import {useDispatch } from "react-redux";
import { Ajouter } from "./Redux/actions";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function CalculIMC(){
    let [id,setId]=useState();
    const [Poids,setPoids]=useState();
    const [Taille,setTaille]=useState();
    const [IMC,setIMC]=useState();
    const [Avis,setAvis]=useState();
    
    const dispatch=useDispatch();

    const CalculIMC=()=>{
        switch(true){
            case (IMC<16.5):
                return setAvis('Denutrition');
            case (IMC>16.5 && IMC<18.5 ):
                return setAvis('Maigreur');
            case (IMC>18.5 && IMC<20 ):
                return setAvis('Corpulance Normale');
            case (IMC>25 && IMC<30 ):
                return setAvis('Surpoids');
            default:
                return setAvis('Obesite morible ou massive');
        }
    }

    const handleSubmit=(e)=>{
        e.preventDefault();
        setIMC(Poids/Math.pow(Taille,2));
        setId(id+=1);
        CalculIMC();
        dispatch(Ajouter({id,Poids,Taille,IMC,Avis}))
    }

    
    return(
        <>
            <form className="container mt-4">
            <div className="mb-3">
                <label htmlFor="poids" className="form-label">
                Poids (en kg)
                </label>
                <input
                type="number"
                className="form-control"
                id="poids"
                onChange={(e) => setPoids(e.target.value)}
                />
            </div>
            <div className="mb-3">
                <label htmlFor="taille" className="form-label">
                Taille (en cm)
                </label>
                <input
                type="number"
                className="form-control"
                id="taille"
                onChange={(e) => setTaille(e.target.value)}
                />
            </div>
            <button className="btn btn-primary" onClick={(e) => handleSubmit(e)}>
                Calculer
            </button>
            <p className="mt-3">
                Votre Indice de Masse Corporelle est: {IMC}
            </p>
            <p>Avis de notre expert : {Avis}</p>
            </form>
        </>
    )
}
