import React, { useState, useEffect } from 'react';

function GestionCompte() {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        role: '',
        password: ''
    });
    const [csrfToken, setCsrfToken] = useState('');

    useEffect(() => {
        // Récupérer le jeton CSRF depuis votre API Laravel
        fetch('/csrf-token')
            .then(response => response.json())
            .then(data => setCsrfToken(data.csrf_token))
            .catch(error => console.error('Erreur lors de la récupération du jeton CSRF:', error));
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://127.0.0.1:8000/api/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'X-CSRF-TOKEN': csrfToken // Inclure le jeton CSRF dans l'en-tête de la requête
                },
                body: JSON.stringify(formData)
            });

            if (response.ok) {
                // Inscription réussie
                console.log('Inscription réussie!');
            } else {
                // Erreur lors de l'inscription
                console.error('Erreur lors de l\'inscription');
            }
        } catch (error) {
            console.error('Erreur lors de la requête:', error);
        }
    };

    return (
        <div className="GestionCompte">
            <form className="form" onSubmit={handleSubmit}>
                <div className="form-row">
                    <div className="form-group">
                        <label htmlFor="nom">Nom</label>
                        <input
                            name="name"
                            id="nom"
                            type="text"
                            placeholder="Nom"
                            required
                            value={formData.name}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Adresse Email</label>
                        <input
                            name="email"
                            id="email"
                            type="email"
                            placeholder="Adresse Email"
                            required
                            value={formData.email}
                            onChange={handleChange}
                        />
                    </div>
                </div>
                <div className="form-row">
                    <div className="form-group">
                        <label htmlFor="role">Role</label>
                        <input
                            name="role"
                            id="role"
                            type="text"
                            placeholder="Role"
                            required
                            value={formData.role}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password1">Mot de passe</label>
                        <input
                            name="password"
                            id="password1"
                            type="password"
                            placeholder="Mot de passe"
                            required
                            value={formData.password}
                            onChange={handleChange}
                        />
                    </div>
                </div>
                <button type="submit">Enregistrer</button>
            </form>
        </div>
    );
}

export default GestionCompte;
