import React, { useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            await axios.get('https://api.sanctum.test/sanctum/csrf-cookie');
            const response = await axios.post('http://127.0.0.1:8000/api/login', {
                email: email,
                password: password
            });
            console.log(response);
            // Redirection ou autre traitement après la connexion réussie
        } catch (error) {
            console.error('Login Error:', error);
            const errorMessage = error.response && error.response.data && error.response.data.message
                ? error.response.data.message
                : 'An unexpected error occurred. Please try again later.';
            Swal.fire({
                icon: 'error',
                title: 'Login Failed',
                text: errorMessage,
            });
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="Mycontainer Main-login">
            <div className="Mycolumn bg-image image"></div>
            <div className="Mycolumn">
                <div className='login'>
                    <h3 className="display-4">Se connecter</h3>
                    <p className="text-muted mb-4">Merci de remplir vos données</p>
                    <form onSubmit={handleSubmit}>
                        <div className="MyMyform-group mb-3">
                            <input
                                id="inputEmail"
                                type="email"
                                placeholder="Adresse email"
                                required=""
                                autoFocus=""
                                className="form-control rounded-pill border-0 shadow-sm px-4"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>
                        <div className="MyMyform-group mb-3">
                            <input
                                id="inputPassword"
                                type="password"
                                placeholder="Mot de passe"
                                required=""
                                className="form-control rounded-pill border-0 shadow-sm px-4 text-primary"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                        <div className="Myform-group Myform-check mb-3">
                            <input id="rememberMe" type="checkbox" className="form-check-input" />
                            <label htmlFor="rememberMe" className="form-check-label">Se souvenir de moi</label>
                        </div>
                        <button type="submit" className="Mybtn" disabled={loading}>{loading ? 'Chargement...' : 'SE CONNECTER'}</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
