import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Card} from 'react-bootstrap';
const jobDetails = [
    {
      title: "Software Developer",
      company: "ABC Tech",
      location: "City, Country",
      jobType: "Full-time",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam ac urna eget..."
    },
    {
      title: "Data Analyst",
      company: "XYZ Analytics",
      location: "Another City, Country",
      jobType: "Part-time",
      description: "Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam..."
    },
    {
      title: "UX/UI Designer",
      company: "Design Co.",
      location: "Different City, Country",
      jobType: "Contract",
      description: "Quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat..."
    },
    // Add 17 more job objects here...
    {
      title: "Network Engineer",
      company: "Tech Solutions Inc.",
      location: "City, Country",
      jobType: "Full-time",
      description: "Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas."
    },
    {
      title: "Marketing Specialist",
      company: "Digital Marketing Agency",
      location: "City, Country",
      jobType: "Part-time",
      description: "Fusce dapibus, tellus ac cursus commodo, tortor mauris condimentum nibh, ut fermentum massa justo sit amet risus."
    },
    {
      title: "Financial Analyst",
      company: "Finance Corp.",
      location: "City, Country",
      jobType: "Full-time",
      description: "Integer posuere erat a ante venenatis dapibus posuere velit aliquet."
    },
    // Add 14 more job objects here...
  ];
  

export default function Offre(){
    return(
        <>
            <Card>
                <Card.Body>
                    <Card.Title>{title}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{company}</Card.Subtitle>
                    <Card.Text>
                    <strong>Location:</strong> {location}<br />
                    <strong>Job Type:</strong> {jobType}
                    </Card.Text>
                    <Card.Text>
                    <strong>Description:</strong> {description}
                    </Card.Text>
                </Card.Body>
            </Card>
        </>
    )
}
