import React, { useEffect, useState } from 'react';

const DonneesTable = () => {
    const [donnees, setDonnees] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:8000/api/donnees')
            .then(response => response.json())
            .then(data => {
                setDonnees(data.factures); // Assurez-vous que la clé 'factures' est correcte
            })
            .catch(error => {
                console.error('Erreur lors de la récupération des données:', error);
            });
    }, []);

    return (
        <div>
            <h1>Données</h1>
            <table style={{borderCollapse: 'collapse', width: '100%'}}>
                <thead>
                    <tr>
                        <th>NomClient</th>
                        <th>NumBonLiv</th>
                        <th>DateBonLiv</th>
                        <th>TypeValidation</th>
                        <th>NomEmetteur</th>
                        <th>NumCheque</th>
                        <th>NumRemise</th>
                        <th>MontantEnc</th>
                    </tr>
                </thead>
                <tbody>
                {donnees.map((facture, index) => (
                <tr key={index} style={{border: '1px solid black'}}>
                    <td>{facture.idClient.NomClient}</td>
                    <td>{facture.NumBonLiv}</td>
                    <td>{facture.NumBonLiv.dateBonLiv}</td>
                    <td>{facture.NumBonLiv.TypeValidation}</td>
                    <td>{facture.idEmetteur.NomEmetteur}</td>
                    <td>{facture.NumCheque}</td>
                    <td>{facture.NumRemise}</td>
                    <td>{facture.remise ? facture.remise.MontantEnc : ''}</td>
                </tr>
            ))}
                </tbody>
            </table>
        </div>
    );
};

export default DonneesTable;
