import React, { useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import '../css/login.css';
import 'bootstrap/dist/css/bootstrap.min.css';


const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/login', {
                email: email,
                password: password
            });

            localStorage.setItem('token', response.data.token);
            console.log('Logged in:', response.data.user);

            window.location.href = '/admin';

            // Optionally, you can display a SweetAlert here before redirecting
            Swal.fire({
                icon: 'success',
                title: 'Login Successful',
                text: 'You have been successfully logged in!',
                didClose: () => {
                    window.location.href = '/admin';
                }
            });
        } catch (error) {
            console.error('Login Error:', error);

            // Récupérer le message d'erreur de la réponse ou afficher un message par défaut
            const errorMessage = error.response && error.response.data && error.response.data.message
                ? error.response.data.message
                : 'An unexpected error occurred. Please try again later.';

            // Afficher une alerte d'erreur de connexion avec SweetAlert
            Swal.fire({
                icon: 'error',
                title: 'Login Failed',
                text: errorMessage,
            });
        }
    };    

    return (
        <div className="Mycontainer Main-login">
          <div className="Mycolumn bg-image image">
          </div>
          <div className="Mycolumn">
                <div className='login'>
                    <h3 class="display-4">Se connecter</h3>
                    <p class="text-muted mb-4">Merci de remplire vos donnees </p>
                    <form>
                        <div class="MyMyform-group mb-3">
                            <input id="inputEmail" type="email" placeholder="Email address" required="" autofocus="" class="form-control rounded-pill border-0 shadow-sm px-4"/>
                        </div>
                        <div class="MyMyform-group mb-3">
                            <input id="inputPassword" type="password" placeholder="Password" required="" class="form-control rounded-pill border-0 shadow-sm px-4 text-primary"/>
                        </div>
                        <div class="Myform-group Myform-check mb-3">
                            <input id="rememberMe" type="checkbox" class="form-check-input"/>
                            <label for="rememberMe" class="form-check-label">Se souvenir de moi</label>
                        </div>
                        <button type="submit" class="Mybtn ">SIGN IN</button>
                    </form>
                </div>
          </div>
        </div>
      );
    };


export default Login;
