import { createBrowserRouter, Navigate } from 'react-router-dom';
import React from 'react';
import Login from '../component/login';
import PageNotFound from '../pages/pageNotFound';
import Layout from '../layout/layout';
import Invoice from '../component/Invoice';
import MainVisualiser from '../component/MainVisualiser';
import GestionCompte from '../component/GestionCompte';

// Router avec ProtectedRoute et Outlet
// Définition de ProtectedRoute
const isAuthenticated = () => {
  // Récupérer tous les cookies
  const cookies = document.cookie.split(';').map(cookie => cookie.trim());
  // Chercher le cookie 'auth_token'
  const authTokenCookie = cookies.find(cookie => cookie.startsWith('auth_token='));
  // Vérifier si le cookie 'auth_token' existe et n'est pas expiré
  if (authTokenCookie) {
    const token = authTokenCookie.split('=')[1];
    // Vous pouvez vérifier ici si le token est valide, par exemple en le validant avec votre serveur
    return true;
  }
  return false;
};


export const router = createBrowserRouter([
  {
    path: '/',
    element: <Login />,
    index: true
  },
  {
    path: '/',
    element: <ProtectedRoute><Layout /></ProtectedRoute>,
    children: [
      { path: 'invoice', element: <Invoice /> },
      { path: 'MainVisualiser', element: <MainVisualiser /> },
      { path: 'GestionCompte', element: <GestionCompte /> }
    ]
  },
  { path: '*', element: <PageNotFound /> }
]);
