import React,{useState,useEffect} from "react";
import {useDispatch } from "react-redux";
import { Ajouter } from "./Redux/actions";

export default function CalculIMC(){
    let [id,setId]=useState();
    const [Poids,setPoids]=useState();
    const [Taille,setTaille]=useState();
    const [IMC,setIMC]=useState();
    const [Avis,setAvis]=useState();
    
    const dispatch=useDispatch();

    const CalculIMC=()=>{
        alert(IMC)
        switch(true){
            case (IMC<16.5):
                return setAvis('Denutrition');
            case (IMC>16.5 && IMC<18.5 ):
                return setAvis('Maigreur');
            case (IMC>18.5 && IMC<20 ):
                return setAvis('Corpulance Normale');
            case (IMC>25 && IMC<30 ):
                return setAvis('Surpoids');
            default:
                return setAvis('Obesite morible ou massive');
        }
    }

    const handleSubmit=(e)=>{
        e.preventDefault();
        setIMC(Poids/Math.pow(Taille,2));
        setId(id+=1);
        dispatch(Ajouter({id,Poids,Taille,IMC,Avis}))
    }

    use
    return(
        <>
            <form>
                <label>Poids (en kg) </label>
                <input type="number" onChange={(e)=>setPoids(e.target.value)}></input>
                <label>Taille (en cm) </label>
                <input type="number" onChange={(e)=>setTaille(e.target.value)}></input>
                <button onClick={(e)=>handleSubmit(e)}>Calculer</button>
                <p>Votre Indice de Masse Corporelle est:{IMC}</p>
                <p>Avis de notre expert :{Avis}</p>
            </form>
        </>
    )
}
