import React,{useState,useEffect} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
export default function Historique(){
    const [id,setId]=useState();
    const [users,setUsers]=useState();
    const [user,setUser]=useState();
    useEffect(
        ()=>{
            fetch('https://jsonplaceholder.typicode.com/users')
            .then((response)=>response.json())
            .then((usersData)=>{console.log('this1',usersData);setUsers(usersData)})
        },[]
    )

    const findUser=()=>{
        const userFound=users.find((item)=>{
            item.id==id;
            console.log(typeof(item.id));
            console.log(typeof(id))
        }
        );
        setUser(userFound);
    }

    return(
        <div className="container mt-4">
            <label className="form-label">Saisir Id</label>
            <input type="number" className="form-control" onChange={(e) => setId(e.target.value)} />
            <button className="btn btn-primary mt-2" onClick={() => findUser()}>Search</button>
            <p className="mt-3">
                <strong>ID:</strong> {user?.id} <strong>Name:</strong> {user?.name} <strong>User Name:</strong> {user?.username}
            </p>
      </div>
    )
}
