const Visualiser = () => {

  return (
    <>
      <center><h1>Visualiser Les Factures</h1></center>
      <div className="container">
        <div className="form-group">
          <label htmlFor="input1">Label 1:</label>
          <input type="text" className="form-control" id="input1" placeholder="Entrez la valeur 1" />
        </div>
        <div className="form-group">
          <label htmlFor="input2">Label 2:</label>
          <input type="text" className="form-control" id="input2" placeholder="Entrez la valeur 2" />
        </div>
      </div>
      <table className="table table-striped table-bordered">
        <thead className="thead-dark">
          <tr>
            <th>Nº de Facture</th>
            <th>Nom Client</th>
            <th>Nº Bon de livraison</th>
            <th>Type de validation</th>
            <th>Colonne 5</th>
            <th>Afficher les Infos</th>
          </tr>
        </thead>
        <tbody>
          {/* Mettez vos données ici */}
        </tbody>
      </table>
    </>
  );
};

export default Visualiser;
