import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card , Row , Col } from 'react-bootstrap';
import OffresData from './Data/OffresData.json';

export default function Offre(){
    return(
        <>
            {OffresData.map((Offre) => {
                return (
                    <Row className="gx-3 gy-3">
                    {OffresData.map((Offre, index) => (
                        <Col key={index} xs={12} md={6} lg={4} >
                            <Card className="m-4" style={{ maxHeight: '18rem' }}>
                                <Card.Body>
                                    <Card.Title>{Offre.title}</Card.Title>
                                    <Card.Subtitle className="mb-2 text-muted">{Offre.company}</Card.Subtitle>
                                    <Card.Text>
                                        <strong>Location:</strong> {Offre.location}<br />
                                        <strong>Job Type:</strong> {Offre.jobType}
                                    </Card.Text>
                                    <Card.Text>
                                        <strong>Description:</strong> {Offre.description}
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
                </Row>
                )
            }
            )
            }
        </>
    )
}
