import '../css/Login.css'
//import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Récupérer le jeton CSRF
            await axios.get('http://localhost:8000/sanctum/csrf-cookie');
            // Envoyer les informations d'identification pour l'authentification
            const response = await axios.post('http://localhost:8000/api/v1/login', {
                email,
                password
            });
            console.log(response.data);
            // Redirection ou autre traitement après la connexion réussie
        } catch (error) {
            console.log(error.response.data.message);
        }
    };

    return (
        <div className="Mycontainer Main-login">
            <div className="Mycolumn bg-image image"></div>
            <div className="Mycolumn">
                <div className='login'>
                    <h3 className="display-4">Se connecter</h3>
                    <p className="text-muted mb-4">Merci de remplir vos données</p>
                    <form onSubmit={handleSubmit}>
                        <div className="MyMyform-group mb-3">
                            <input
                                id="inputEmail"
                                type="email"
                                placeholder="Adresse email"
                                required=""
                                autoFocus=""
                                className="form-control rounded-pill border-0 shadow-sm px-4"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>
                        <div className="MyMyform-group mb-3">
                            <input
                                id="inputPassword"
                                type="password"
                                placeholder="Mot de passe"
                                required=""
                                className="form-control rounded-pill border-0 shadow-sm px-4 text-primary"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                        <div className="Myform-group Myform-check mb-3">
                            <input id="rememberMe" type="checkbox" className="form-check-input"/>
                            <label htmlFor="rememberMe" className="form-check-label">Se souvenir de moi</label>
                        </div>
                        <button type="submit" className="Mybtn">SE CONNECTER</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
