import React,{ useState} from "react";
import { useDispatch, useSelector } from "react-redux";

export default function AfficherFilms(){
    const [numSearched,setNumSearched]=useState();
    const [realisateurs,setRealisateurs]=useState();
    const [FilmsFound,setFilmsFound]=useState([]);
    const [realisateur,setRealisateur]=useState();
    const [FilmsOfRealisateur,setFilmsOfRealisateur]=useState();

    const Films=useSelector((state)=>state.Films);
    const Realisateurs=useSelector((state)=>state.Realisateurs);
    const dispatch=useDispatch();

    const Search=()=>{
        setFilmsFound([...FilmsFound,...Films]);
        setRealisateurs([...realisateurs,]);
        const SearchRealisateur=FilmsFound.find((item)=>item.numR===numSearched);
        console.log(FilmsFound)
        console.log(Realisateurs)
        console.log(SearchRealisateur)
    //     if(!SearchRealisateur){
    //         alert('Id Film Not Found !')
    //     }
    //     else{
    //         setRealisateur(realisateurs.find((item)=>item.numR==numSearched))
    //         setFilmsOfRealisateur(Films.filter((item)=>item.codeF==realisateur.numR))
    //     }
    }
    return(
        <>
            <p>Saisir Numero Realisateur</p>
            <input type='text' onChange={(e)=>setNumSearched(e.target.value)}/>
            <button onClick={Search}>Search</button>
            <table>
                <thead>
                    <tr>
                        <td>Num Realisateur</td>
                        <td>Nom</td>
                        <td>Prenom</td>
                        <td>Total Duree Film</td>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{realisateur?.numR}</td>
                        <td>{realisateur?.nom}</td>
                        <td>{realisateur?.duree}</td>
                        <td>{realisateur?.realisateur}</td>
                    </tr>
                </tbody>
                <thead>
                    <tr>
                        <td>Code Film</td>
                        <td>Titre</td>
                        <td>Duree</td>
                        <td>Realisateur</td>
                    </tr>
                </thead>
                <tbody>
                    {
                        FilmsOfRealisateur?.map((item,index)=>{
                            return(
                                <tr key={index}>
                                <td>{item.codeF}</td>
                                <td>{item.titre}</td>
                                <td>{item.duree}</td>
                                <td>{item.realisateur}</td>
                                <td><button>Supprimer</button></td>
                                </tr>
                            )
                        }
                        )
                    }
                </tbody>
            </table>
        </>
    )
}
