import { Alert } from "react-bootstrap"

const initialState={
    Offers:[
        {
          title: "Software Developer",
          company: "ABC Tech",
          location: "City, Country",
          jobType: "Full-time",
          description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam ac urna eget..."
        }
      ]      
}

export default function ReducerJobs(state=initialState,action){
  switch(action.payload){
    case 'SubmitOffer':
      return alert('hey')
    default:
      return state
  }
}
