import React,{useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import {SubmitAnOffer} from './Redux/actions'

export default function SubmitOffer(){
    const [JobTitle,setJobTitle]=useState();
    const [company,setCompany]=useState();
    const [Location,setLocation]=useState();
    const [JobType,setJobType]=useState();
    const [Description,setDescription]=useState();

    const dispatch=useDispatch()

    const handleSubmit=(e)=>{
      e.preventDefault();
      dispatch(SubmitAnOffer({
        title: JobTitle,
        company: company,
        location: Location,
        jobType: JobType,
        description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam ac urna eget..."
      }))
    }

    const handleReset=()=>{
        setJobTitle();
        setLocation();
        setJobType();
        setDescription();
    }

    return (
        <>
        <Link to={'/home'}>
            <button className="btn btn-light w-100 font-weight-bold text-primary">Home</button>
        </Link>
        <div className="container">
          <div className="row justify-content-center mt-1">
            <div className="col-md-6">
                <h1 className="text-center mb-2">Submit An Offer</h1>
                <form onSubmit={(e) => handleSubmit(e)} onReset={(e)=>handleReset(e)}>
                <div className="mb-4">
                  <label htmlFor="jobTitle" className="form-label text-white">
                    Job Title
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="jobTitle"
                    onChange={(e) => setJobTitle(e.target.value)}
                  />
                </div>
                <div className="mb-4">
                  <label htmlFor="companyLocation" className="form-label text-white">
                    Company Location
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="companyLocation"
                    onChange={(e) => setLocation(e.target.value)}
                  />
                </div>
                <div className="mb-4">
                  <label htmlFor="Job Type" className="form-label text-white">
                  Job Type
                  </label>
                  <select className="form-select" id="Job Type" onChange={(e) => setJobType(e.target.value)}>
                    <option>Full-Time</option>
                    <option>Part-Time</option>
                    <option>Freelance</option>
                  </select>
                </div>
                <div className="mb-4">
                  <label htmlFor="Description" className="form-label text-white">
                    Job Description
                  </label>
                  <textarea className="form-control" rows={3} onChange={(e) => setDescription(e.target.value)}/>
                </div>
                <button type="submit" className="btn btn-primary w-100">
                  Submit
                </button>
                <button type="reset" className="btn custom-reset-btn w-100 text-white">
                  Reset
                </button>
              </form>
            </div>
          </div>
        </div>
        </>
      )
    ;      
}
