import React, { useState } from 'react';

const Table = ({ data, itemsPerPage }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedRow, setSelectedRow] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [isDetailModalOpen, setIsDetailModalOpen] = useState(false);

  const totalPages = Math.ceil(data.length / itemsPerPage);

  const handleChangePage = (page) => {
    setCurrentPage(page);
  };

  const handleShowModal = (rowData) => {
    setSelectedRow(rowData);
    setIsDetailModalOpen(true);
  };

  const handleCloseDetailModal = () => {
    setSelectedRow(null);
    setIsDetailModalOpen(false);
  };

  const handleDeleteRow = () => {
    setIsDeleteModalOpen(true);
    setIsDetailModalOpen(false);
  };

  const confirmDelete = () => {
    console.log("Suppression de la ligne:", selectedRow);
    setIsDeleteModalOpen(false);
    setSelectedRow(null);
    // TODO: Supprimer la ligne de la base de données ou de la source de données
  };

  const renderPagination = () => {
    const pages = [];
    const maxPagesToShow = 5;
    const startPage = Math.max(1, currentPage - Math.floor(maxPagesToShow / 2));
    const endPage = Math.min(totalPages, startPage + maxPagesToShow - 1);

    for (let i = startPage; i <= endPage; i++) {
      pages.push(
        <li className={`page-item ${currentPage === i ? 'active' : ''}`} key={i}>
          <button className="page-link" onClick={() => handleChangePage(i)}>{i}</button>
        </li>
      );
    }

    const previousButton = (
      <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
        <button className="page-link" onClick={() => handleChangePage(currentPage - 1)}>Previous</button>
      </li>
    );

    const nextButton = (
      <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
        <button className="page-link" onClick={() => handleChangePage(currentPage + 1)}>Next</button>
      </li>
    );

    return (
      <nav>
        <ul className="pagination">
          {previousButton}
          {pages}
          {nextButton}
        </ul>
      </nav>
    );
  };
 
  const handleCancelDelete = () => {
    setIsDeleteModalOpen(false);
    setIsDetailModalOpen(true);
  };

  const renderTableData = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return data.slice(startIndex, endIndex).map((item, index) => (
      <tr key={startIndex + index}>
        <td>{item.column1}</td>
        <td>{item.column2}</td>
        <td>{item.column3}</td>
        <td>{item.column4}</td>
        <td>{item.column5}</td>
        <td>
          <button className="btn btn-primary" onClick={() => handleShowModal(item)}>Plus d'infos</button>
        </td>
      </tr>
    ));
  };

  const renderModal = () => {
    if (!isDetailModalOpen) return null;

    return (
      <div className="modal">
        <div className="modal-content">
          <span className="close" onClick={handleCloseDetailModal}>&times;</span>
          <h2>Détails</h2>
          {/* Les détails du modal */}
          <div className="modal-delete">
            <center>
              <button onClick={handleDeleteRow}>Supprimer</button>
            </center>
          </div>
        </div>
      </div>
    );
  };

  const renderDeleteConfirmationModal = () => {
    if (!isDeleteModalOpen) return null;

    return (
      <div className="modal">
        <div className="modal-content">
          <span className="close" onClick={handleCancelDelete}>&times;</span>
          <h2>Confirmer la suppression</h2>
          <p>Êtes-vous sûr de vouloir supprimer cet enregistrement ?</p>
          <button onClick={confirmDelete}>Confirmer</button>
          <button onClick={handleCancelDelete}>Annuler</button>
        </div>
      </div>
    );
  };

  return (
    <div className="table-container">
      <table className="table">
        <thead>
          {/* Entêtes du tableau */}
        </thead>
        <tbody>
          {renderTableData()}
        </tbody>
      </table>
      {renderPagination()}
      {renderModal()}
      {renderDeleteConfirmationModal()}
    </div>
  );
};

export default Table;
