import React, { useState } from 'react';
import 'C:/Users/Asus ZenBook 14/Desktop/My-github/Invoice Project/src/css/Table.css'; // Import custom CSS file for styling

const TableWithData = ({ data, itemsPerPage }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedRow, setSelectedRow] = useState(null);

  const totalPages = Math.ceil(data.length / itemsPerPage);

  const handleChangePage = (page) => {
    setCurrentPage(page);
  };

  const handleShowModal = (rowData) => {
    setSelectedRow(rowData);
  };

  const handleCloseModal = () => {
    setSelectedRow(null);
  };

  const renderTableData = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return data.slice(startIndex, endIndex).map((item, index) => (
      <tr key={startIndex + index}>
        <td>{item.column1}</td>
        <td>{item.column2}</td>
        <td>{item.column3}</td>
        <td>{item.column4}</td>
        <td>{item.column5}</td>
        <td>
          <button className="btn btn-primary" onClick={() => handleShowModal(item)}>Plus d'infos</button>
        </td>
      </tr>
    ));
  };

  const renderPagination = () => {
    // Pagination rendering logic...
  };

  const renderModal = () => {
    if (!selectedRow) return null;

    return (
      <div className="modal">
        <div className="modal-content">
          <span className="close" onClick={handleCloseModal}>&times;</span>
          <div>
            <h2>Details</h2>
            <p>Nº de Facture: {selectedRow.column1}</p>
            <p>Nom Client: {selectedRow.column2}</p>
            <p>Nº Bon de livraison: {selectedRow.column3}</p>
            <p>Type de validation: {selectedRow.column4}</p>
            <p>Mode de Règlement: {selectedRow.column5}</p>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="table-container">
      <table className="table">
        <thead className="thead-dark">
          <tr>
            <th>Nº de Facture</th>
            <th>Nom Client</th>
            <th>Nº Bon de livraison</th>
            <th>Type de validation</th>
            <th>Mode de Règlement</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {renderTableData()}
        </tbody>
      </table>
      {renderPagination()}
      {renderModal()}
    </div>
  );
};

export default TableWithData;
