import React, { useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap'; // Importation des composants de react-bootstrap
import '../css/login.css'; // Assurez-vous de créer un fichier Form.css pour les styles

const Login = () => {
    return (
        <Container fluid className="fade-in">
            <Row noGutters>
                <Col md={6} className="d-none d-md-flex bg-image"></Col>
                <Col md={6} className="bg-light test">
                    <div className="login d-flex align-items-center py-5">
                        <Container>
                            <Row>
                                <Col lg={10} xl={7} className="mx-auto">
                                    <h3 className="display-4">Login</h3>
                                    <p className="text-muted mb-4">Merci de remplir vos données</p>
                                    <Form>
                                        <Form.Group controlId="inputEmail" className="mb-3">
                                            <Form.Control type="email" placeholder="Email address" required autoFocus className="rounded-pill border-0 shadow-sm px-4" />
                                        </Form.Group>
                                        <Form.Group controlId="inputPassword" className="mb-3">
                                            <Form.Control type="password" placeholder="Password" required className="rounded-pill border-0 shadow-sm px-4 text-primary" />
                                        </Form.Group>
                                        <Form.Group controlId="rememberMe" className="form-check mb-3">
                                            <Form.Check type="checkbox" label="Remember Me" className="form-check-input" />
                                        </Form.Group>
                                        <Button type="submit" className="btn btn-primary btn-block text-uppercase mb-2 rounded-pill shadow-sm">Sign in</Button>
                                    </Form>
                                </Col>
                            </Row>
                        </Container>
                    </div>
                </Col>
            </Row>
        </Container>
    );
};

export default Login;
