import React, { useState } from 'react';
import '../../css/GestionCompte.css'; // Assurez-vous d'avoir ce fichier CSS dans le même dossier

const GestionCompte = () => {
  const [activeBox, setActiveBox] = useState(null);

  const handleBoxClick = (boxId) => {
    if (activeBox === boxId) {
      // Si la boîte cliquée est déjà active, désactivez-la
      setActiveBox(null);
    } else {
      // Sinon, activez la boîte cliquée
      setActiveBox(boxId);
    }
  };

  return (
    <div className="container GestionCompte">
      <div className={`box ${activeBox === 'create' ? 'active' : ''}`} onClick={() => handleBoxClick('create')}>
        {/* SVG et contenu ici */}
        <div>Créer un utilisateur</div>
      </div>
      <div className={`box ${activeBox === 'manage' ? 'active' : ''}`} onClick={() => handleBoxClick('manage')}>
        {/* SVG et contenu ici */}
        <div>Gérer</div>
      </div>
      <div className={`box ${activeBox === 'add-emitter' ? 'active' : ''}`} onClick={() => handleBoxClick('add-emitter')}>
        {/* SVG et contenu ici */}
        <div>Ajouter un émetteur</div>
      </div>
    </div>
  );
};

export default GestionCompte;
