import React, { useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import '../css/Login.css'
//import 'bootstrap/dist/css/bootstrap.min.css';


const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Récupérer le jeton CSRF depuis votre application Laravel
            const csrfToken = document.querySelector('meta[name="csrf-token"]').getAttribute('content');

            // Effectuer la demande de connexion en incluant le jeton CSRF dans les en-têtes
            const response = await axios.post('http://127.0.0.1:8000/api/login', {
                email: email,
                password: password
            }, {
                headers: {
                    'X-CSRF-TOKEN': csrfToken // Inclure le jeton CSRF dans les en-têtes
                }
            });

            // Redirection basée sur le rôle
            window.location.href = '/MainVisualiser';
        } catch (error) {
            // Gérer les erreurs de connexion ici
        }
    };



    return (
        <div className="Mycontainer Main-login">
          <div className="Mycolumn bg-image image">
          </div>
          <div className="Mycolumn">
                <div className='login'>
                    <h3 class="display-4">Se connecter</h3>
                    <p class="text-muted mb-4">Merci de remplire vos donnees </p>
                    <form onSubmit={handleSubmit}>
                        <div class="MyMyform-group mb-3">
                           <input id="inputEmail" type="email" placeholder="Email address" required="" autofocus="" 
                                class="form-control rounded-pill border-0 shadow-sm px-4"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                             />                      
                        </div>
                        <div class="MyMyform-group mb-3">                
                           <input id="inputPassword" type="password" placeholder="Password" required="" 
                                    class="form-control rounded-pill border-0 shadow-sm px-4 text-primary"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                            />                        
                        </div>
                        <div class="Myform-group Myform-check mb-3">
                            <input id="rememberMe" type="checkbox" class="form-check-input"/>
                            <label for="rememberMe" class="form-check-label">Se souvenir de moi</label>
                        </div>
                        <button type="submit" class="Mybtn ">SIGN IN</button>
                    </form>
                </div>
          </div>
        </div>
      );
    };


export default Login;
