import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/login', {
                email: email,
                password: password
            });
            if (response.data.status === 'success') {
                // Authenticated, handle success
                console.log('User logged in successfully');
            } else {
                // Error in login, handle accordingly
                console.log('Error in login:', response.data.data);
            }
        } catch (error) {
            console.error('Error during login:', error);
        }
    };

    return (
        <div className="Mycontainer Main-login">
            <div className="Mycolumn bg-image image"></div>
            <div className="Mycolumn">
                <div className='login'>
                    <h3 class="display-4">Se connecter</h3>
                    <p class="text-muted mb-4">Merci de remplir vos donn√©es</p>
                    <form onSubmit={handleLogin}>
                        <div class="MyMyform-group mb-3">
                            <input
                                id="inputEmail"
                                type="email"
                                placeholder="Adresse Email"
                                required
                                autoFocus
                                class="form-control rounded-pill border-0 shadow-sm px-4"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>
                        <div class="MyMyform-group mb-3">
                            <input
                                id="inputPassword"
                                type="password"
                                placeholder="Mot de passe"
                                required
                                class="form-control rounded-pill border-0 shadow-sm px-4 text-primary"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                        <div class="Myform-group Myform-check mb-3">
                            <input id="rememberMe" type="checkbox" class="form-check-input" />
                            <label for="rememberMe" class="form-check-label">Se souvenir de moi</label>
                        </div>
                        <button type="submit" class="Mybtn">Se connecter</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
