import { createBrowserRouter, Navigate, Outlet } from 'react-router-dom';
import React from 'react';
import Layout from './Components/Header/Layout';
import Invoice from './Components/Invoice';
import Login from './Components/Login';
import Visualiser from "./Components/Visualiser/Visualiser";
import Suivi from "./Components/Suivi/Suivi";
import GestionCompte from "./Components/GestionComptes/GestionCompte";

const ProtectedRoute = ({ children }) => {
  const isAuthenticated = () => {
    const cookies = document.cookie.split(';').map(cookie => cookie.trim());
    const authTokenCookie = cookies.find(cookie => cookie.startsWith('auth_token='));
    if (authTokenCookie) {
      const token = authTokenCookie.split('=')[1];
      return true;
    }
    return false;
  };
  return isAuthenticated() ? children : <Navigate to="/" />;
};

export const router = createBrowserRouter([
  {
    path: '/',
    element: <Login />,
    index: true
  },
  {
    path: '/',
    element: <Layout />,
    children: [
      { path: 'invoice', element: <Invoice /> },
      { path: 'Visualiser', element: <Visualiser /> },
      { path: 'GestionCompte', element: <GestionCompte /> },
      { path: 'Suivi', element: <Suivi/> }
    ]
  },
  { path: '*', element: <PageNotFound /> }
]);
