const initialeState={Calculs:[],Films:[],}
export default function ReducerIMC(state=initialeState,action){
    switch(action.type){
        case 'Ajouter':
            return {...state,Calculs:[...state.Calculs,action.payload]};
        case 'Reset':
            return {Calculs:[]};
        case 'AjouterFilm':
            return {...state,Films:[...state.Films,action.payload]};
        case 'AjouterRealisateur':
            return {...state,Realisateurs:[...state.Realisateurs,action.payload]}
        default:
            return state
    }
}
