import React, { useState } from 'react';
import '../css/invoice.css'; // Assurez-vous de créer un fichier Form.css pour les styles

const Invoice = () => {
  const [formData, setFormData] = useState({
    input1: '',
    input2: '',
    select1: '',
    date1: '',
    // Ajoutez les autres champs d'entrée de la même manière
    // input3: '', input4: '', ..., input16: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ajoutez ici la logique pour soumettre le formulaire
    console.log(formData);
  };

  // Tableau de configuration des champs
  const fieldsConfig = [
    { label: 'Nº de Facture', type: 'text', name: 'input1' },
    { label: 'Nom Client', type: 'text', name: 'input2' },
    { label: 'Type de validation', type: 'select', name: 'select1', options: ['Option 1', 'Option 2', 'Option 3'] },
    { label: 'Date de Livraison', type: 'date', name: 'date1' },
    { label: 'Nº de Facture', type: 'text', name: 'input1' },
    { label: 'Nom Client', type: 'text', name: 'input2' },
    { label: 'Type de validation', type: 'select', name: 'select1', options: ['Option 1', 'Option 2', 'Option 3'] },
    { label: 'Date de Livraison', type: 'date', name: 'date1' },
    { label: 'Nº de Facture', type: 'text', name: 'input1' },
    { label: 'Nom Client', type: 'text', name: 'input2' },
    { label: 'Type de validation', type: 'select', name: 'select1', options: ['Option 1', 'Option 2', 'Option 3'] },
    { label: 'Date de Livraison', type: 'date', name: 'date1' },
    // Ajoutez d'autres champs de la même manière
    // { label: 'Label du champ', type: 'type_du_champ', name: 'nom_du_champ', options: ['Option 1', 'Option 2', ...] }
  ];

  return (
    <div>
      <center><h3>Facture</h3></center>
      <form className="form" onSubmit={handleSubmit}>
      <div className="input-container">
  {/* Boucle pour générer les champs d'entrée */}
  {fieldsConfig.map((field, index) => (
    <div key={index} className='item'>
      <label htmlFor={field.name}>
        {field.label}
      </label>
      {/* Générez le champ d'entrée en fonction du type */}
      {field.type === 'select' ? (
        <select
          name={field.name}
          value={formData[field.name]}
          onChange={handleChange}
        >
          {/* Générez les options du select */}
          {field.options.map((option, optionIndex) => (
            <option key={optionIndex} value={option}>
              {option}
            </option>
          ))}
        </select>
      ) : (
        <input
          type={field.type}
          name={field.name}
          value={formData[field.name]}
          onChange={handleChange}
          placeholder={`field.nameInput ${index + 1}`}
        />
      )}
    </div>
  ))}
</div>

        <div className="button-container">
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default Invoice;
