import React,{useState} from "react";
import {useDispatch } from "react-redux";

export default function(){
    const [id,setId]=useState();
    const [Poids,setPoids]=useState();
    const [Taille,setTaille]=useState();
    const [IMC,setIMC]=useState();
    const [Avis,setAvis]=useState();
    
    const dispatch=useDispatch();

    const CalculIMC=()=>{
        switch(IMC){
            case IMC<16.5:
                return 'Denutrition';
            case IMC>16.5 && IMC<18.5:
                return 'Maigreur';
            case IMC>18.5 && IMC<20:
                return 'Corpulance Normale';
            case IMC>25 && IMC<30:
                return 'Surpoids';
            default:
                return 'Obesite morible ou massive';
        }
    }

    const handleSubmit=(e)=>{
        e.preventDefault();
        setIMC(Poids/Taille.pow(2));
        CalculIMC();
        setId(id+=1);
        dispatch(Ajouter({id,Poids,Taille,IMC,Avis}))
    }
    return(
        <>
            <form>
                <label>Poids (en kg) </label>
                <input type="number" onChange={(e)=>setPoids(e.target.value)}></input>
                <label>Taille (en cm) </label>
                <input type="number" onChange={(e)=>setTaille(e.target.value)}></input>
                <button onClick={(e)=>handleSubmit(e)}>Calculer</button>
                <p>Votre Indice de Masse Corporelle est:{IMC}</p>
                <p>Avis de notre expert :{Avis}</p>
                
            </form>
        </>
    )
}
